mapper.select:
SELECT t.en_no, t.enterprise as en_name, sum( 1 ) AS sum_num, sum( CASE WHEN STATUS = 1 THEN 1 ELSE 0 END ) AS in_num , sum( CASE WHEN STATUS != 1 THEN 1 ELSE 0 END ) AS out_num , ROUND(sum( CASE WHEN STATUS = 1 THEN 1 ELSE 0 END )/sum( 1 )*100,2) as rate FROM bns_user t WHERE t.en_no IS NOT NULL
and date_format(t.entry_date,'%Y') = #{year}
and date_format(t.entry_date,'%Y-%c') = concat(#{year},'-',#{month})
and t.entry_date >= #{startDate}
and t.entry_date <= #{endDate}
and t.en_no in (${enNo} )
GROUP BY t.en_no, t.enterprise order by rate desc

select id, user_no, user_name, en_no, en_name, type, create_time, sum_num, in_num, out_num, rate from bns_user_report where 1=1
user_no =#{userNo,jdbcType=VARCHAR}
type =#{type,jdbcType=TINYINT}
order by data_time ,en_no
