mapper.select:
select * from sys_user a where 1 =1 AND validate_state = 1

  AND  a.id=#{id,jdbcType=VARCHAR}


  AND  a.USER_NO=#{userNo,jdbcType=VARCHAR}

SELECT a.id, a.user_no, a.user_name, b.id as role_id, b.role_name, b.role_code, a.department, a.job, a.mobile, date_format(a.create_time,'%Y-%m-%d') AS create_time FROM sys_user a LEFT JOIN sys_role_user c ON a.id = c.target_id LEFT JOIN sys_role b ON c.role_id = b.ID WHERE a.validate_state =1 and c.id !=1
and b.ID=#{roleId,jdbcType=BIGINT}
and a.user_name=#{userName}
order by a.id
